    private void btn_LoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_LoginActionPerformed
        
        
   
        
        if(txt_username.getText().isEmpty() || txt_password.getText().isEmpty() || combo.getSelectedItem().toString().isEmpty())
        {
        JOptionPane.showMessageDialog(null, " Please enter all fields !");
        }
        else
        {
        String sql = " select * from USER where( USERNAME = ? and PASSWORD = ? and USER_TYPE = ? )";

        try{

            pst=conn.prepareStatement(sql);
            
            pst.setString(1,txt_username.getText());
            pst.setString(2,txt_password.getText());
            pst.setString(3,combo.getSelectedItem().toString());

            rs = pst.executeQuery();


            String access = (combo.getSelectedItem().toString());

            if((rs.next())){
                
                if(access == "Admin")
                {
                JOptionPane.showMessageDialog(null, " Login Successfull (Admin mode) !");
                Admin_dashboard d = new Admin_dashboard();
                
                d.setVisible(true);
                this.dispose();
                }
                
                else 
                {
                JOptionPane.showMessageDialog(null, " WELCOME " + txt_username.getText() + " ! ");
              
                Table t = new Table(txt_username.getText());
                t.setVisible(true);
                String query = " UPDATE USER set LOGIN = datetime('now') where USERNAME = ?"; 
                pst = conn.prepareStatement(query);
                pst.setString(1,txt_username.getText());
                pst.execute();
                this.dispose();
                }

            }

            else {
                JOptionPane.showMessageDialog(null, "Username & Password are incorrect");
            }

        } catch(Exception e){

            JOptionPane.showMessageDialog(null,e);
        }
        finally{

            try{
                rs.close();
                pst.close();

            }catch(Exception e)
            {

            }

        }
        }
        

    }//GEN-LAST:event_btn_LoginActionPerformed

