    private void show_pswActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_show_pswActionPerformed
        if(show_psw.isSelected())
        {
            txt_password.setEchoChar((char)0);
        }
        else{
            txt_password.setEchoChar('*');
        }
    }//GEN-LAST:event_show_pswActionPerformed

